                    Quantity = cartLine.Quantity + 1,
                    ProductId = cartLine.ProductId,
                    Price = cartLine.Price
                };
                state.CartLineViewModels.Remove(cartLine);
                state.CartLineViewModels.Add(line);
            }

            navigationManager.NavigateTo("/cart");
        }

    

